<html><head></head><body><?phpinclude '../../../_logger.php';function p2fconversion_handler($file_path,$file_name){    $file_path=str_replace('../../',"",$file_path);	    logit("p2fHandler.php","File Path is....  ".$file_path );	logit("p2fHandler.php","File Name is .... ".$file_name );	$p2f = new COM("Print2Flash3.Server2");	$logFileName=realpath("../".$file_path)."/Log".$file_name.".txt";	$absPath=realpath(".././".$file_path.'/@@-OriginalDocs-@@/'.$file_name);	logit("p2fHandler.php","AbsPath is .... ".$absPath );	$logDir="";	$pagepath="../../_sfp__".$file_name;	  if ($p2f)        {				$p2f->DefaultProfile->OutputFormat = 2;				if(!is_dir($file_path."/@@-Thumbnails-@@/"))				{					mkdir($file_path."/@@-Thumbnails-@@/");				}                $p2f->DefaultProfile->ThumbnailPageRange="All";                $p2f->DefaultProfile->ThumbnailImageWidth=140;                $p2f->DefaultProfile->ThumbnailJpegQuality=80;                $p2f->DefaultProfile->ThumbnailImageHeight=140;                $p2f->DefaultProfile->ThumbnailFormat=1;	                $p2f->DefaultProfile->ThumbnailFileName=realpath(".././".$file_path).'/@@-Thumbnails-@@/'.$file_name.'_files\thumbnail_%page%.%ext%';				$p2f->DefaultProfile->PageFileName = realpath( "../".$file_path ).'/_sfp__'.$file_name.'/page_%page%.swf';				$p2f->DefaultBatchProcessingOptions->UseAutomation=8;                try				{					$p2fBPO->LoggingLevel=1;						$p2fBPO = $p2f->DefaultBatchProcessingOptions;						                $p2fBPO->BeforePrintingTimeout = 300000;					$p2fBPO->ActivityTimeout=300000;					$p2fBPO->CreateLogFile=true;					$p2fBPO->LogFileName= realpath("../".$file_path)."/Log".$file_name.".txt";					$logfilename=realpath(".././".$file_path)."/Log".$file_name.".txt";						$message = "<result><status>2. Starting conversion</status><message></message></result>";					logit("print2flash.php",$message);					$p2f->ConvertFile($absPath);					logit("print2flash.php","3. Finished converting ".$file_path." to ".$swffile_fs);					$message =  "<result><status>OK</status><message>$swffile_fs</message></result>";					return $message="Finished Converting";				}				catch(Exception $e)				{					$message = "<result><status>4. Error</status><message>".$e->getMessage()."</message></result>";					logit("print2flash.php",$message);					return $e;				}        }		  		}  ?></body></html>